package com.novelot.progressdemo;import android.app.Activity;import android.os.Bundle;public class ProgressDemoActivity extends Activity {    private Progress progress;    public void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        progress = new Progress(this);        setContentView(progress);        progress.setOnFinishLinstener(new Progress.onFinishLinstener() {            @Override            public void onSetFinishLinstener() {                finish();            }        });    }}